/* ---- Optional: Align icons perfectly ---- */
.cssHeaderGrp {
    background-color: #f2f2f2 !important; /* light grey */
  gap: 15px;
  padding: 5px 15px;
}

.cssNarrow  {
  gap: 2px;
}
.cssGreyBG {
    background-color: #f2f2f2 !important; /* light grey */
}
/*Hide CTRL */
.ctrlHide{
display: none;
}

/* Make 'cssShrink' inputs narrower */
.cssShrink input.form-control {
  width: 37px !important;     /* adjust width as needed */
  min-width: 11px !important; /* prevents collapsing too far */
  text-align: center;         /* keeps text centered */
 
}
.cssMed input.form-control {
  width: 150px !important;     /* adjust width as needed */
 
  
}
.cssMed2 input.form-control {
  width: 50px !important;     /* adjust width as needed */
 
  
}


/* hide */
.hideCTRL{
    display: none;
}
/* Wider Form */
@media screen and (min-width: 0) {
    .nx-form {
        width:100%;
        padding: 20px 15px
    }
}
 
@media screen and (min-width: 768px) {
    .nx-form {
        width:100%;
        padding: 20px 15px
    }
}
 
@media screen and (min-width: 940px) {
    .nx-form {
        width:900px;
        padding: 20px 15px;
    }
}
 
@media screen and (min-width: 1280px) {
    .nx-form {
        width:1200px;
        padding: 25px 15px;
        max-width: 1200px;
    }
}
/*----------------------------*/
.CSSmyRS .nx-row > div:nth-child(-n+5) {
  width: 190px !important;
  max-width: 320px !important;
  flex: 0 0 190px !important;
}
/* Target the 6th control */
.CSSmyRS .nx-row > div:nth-child(1) {
  width: 44px !important;
  max-width: 320px !important;
  flex: 0 0 44px !important;
}
/* Target the 6th control */
.CSSmyRS .nx-row > div:nth-child(6) {
  width: 310px !important;
  max-width: 320px !important;
  flex: 0 0 310px !important;
}
