.hr-line {
  border: none;
  border-top: 2px solid #ccc;
  margin: 10px 0; /* Adjust spacing as needed */
}

.nx-group-control-body .my-colored-section {
  background-color: #f0f8ff; /* Example: AliceBlue */
  padding: 15px; /* Add padding for better visual */
  border-radius: 5px; /* Optional: rounded corners */
}

.nx-group-control-container.cssGrpLOCK {
  opacity: 0.9;
  pointer-events: none;
  filter: grayscale(100%);
}

.nx-form-label .nx-title {
    font-weight:bold;
}

.grp-travel-expense .row:nth-child(odd)
{
    background-color: lightgray;
    border-bottom: 1px solid black;
}

.grp-travel-expense .row:nth-child(even)
{
    background-color: #F5F5F5;
    border-bottom: 1px solid black;
}

.grp-training-expense .row:nth-child(odd)
{
    background-color: lightgray;
    border-bottom: 1px solid black;
}

.grp-training-expense .row:nth-child(even)
{
    background-color: #F5F5F5;
    border-bottom: 1px solid black;
}

.cssGreen {
    font-weight: bold;
    background-color: #008000;
    border-radius: 6px;
    color: #fff;
    display: inline-block;
    font-family: Arial, sans-serif;
    padding: 5px 25px;
}

