/* Target the specific multi-choice container */
.cssMchoice10cols .nx-checkbox-group {
    display: grid;                     /* use grid layout */
    grid-template-columns: repeat(5, 1fr); /* 2 equal columns */
    gap: 10px 20px;                    /* row-gap 10px, column-gap 20px */
}

/* Optional: make each checkbox label align nicely */
.cssMchoice10cols .nx-checkbox {
    display: flex;
    align-items: center;
    padding: 4px 0;
}

/* RiskRate Center the whole radio group */
.cssriskrate .nx-radio-group {
    justify-content: center;
}

/* Make each option act like a block */
.cssriskrate .nx-radio {
    margin: 0 4px;
}

/* Base styling for the clickable label container */
.cssriskrate .nx-radio-container {
    min-width: 120px;
    padding: 6px 10px;
    text-align: center;
    font-weight: bold;
    border-radius: 2px;
}

/* Hide the default radio circle */
.cssriskrate .nx-radio-faux {
    display: none;
}

/* Ensure label text is centered */
.cssriskrate .nx-radio-label {
    display: block;
    width: 100%;
}

/* LOW */
.cssriskrate input[value="LOW"] + .nx-radio-container {
    background-color: lawngreen;
    color: #000;
}

/* SIGNIFICANT */
.cssriskrate input[value="SIGNIFICANT"] + .nx-radio-container {
    background-color: orange;
    color: #000;
}

/* HIGH */
.cssriskrate input[value="HIGH"] + .nx-radio-container {
    background-color: red;
    color: #fff;
}

/* EXTREME */
.cssriskrate input[value="EXTREME"] + .nx-radio-container {
    background-color: black;
    color: #fff;
}

/* Optional: visual feedback when selected */
.cssriskrate input:checked + .nx-radio-container {
    outline: 2px solid #333;
}

/* EXTREME – force white text */
.cssriskrate input[value="EXTREME"] + .nx-radio-container  {
    color: rgb(255, 255, 255) !important;
   background-color: black!important;

}
/* EXTREME – force white text */
.cssriskrate input[value="EXTREME"] + .nx-radio-container .nx-radio-label {
    color: #fff !important;
}
