/* Status colors */
.cssGreen > .nx-valign-middle > .nx-theme-richtext > div > div:first-child {
    font-weight: bold;
    background-color: #008000;
    border-radius: 6px;
    color: #fff;
    display: inline-block;
    font-family: Arial, sans-serif;
    padding: 5px 25px;
}
.cssOrange > .nx-valign-middle > .nx-theme-richtext > div > div:first-child {
    font-weight: bold;
    background-color: #f6a600;
    border-radius: 6px;
    color: #fff;
    display: inline-block;
    font-family: Arial, sans-serif;
    padding: 5px 25px;
}
.cssRed > .nx-valign-middle > .nx-theme-richtext > div > div:first-child {
    font-weight: bold;
    background-color: #EC2D0E;
    border-radius: 6px;
    color: #fff;
    display: inline-block;
    font-family: Arial, sans-serif;
    padding: 5px 25px;
}


/*Hide CTRL */
.ctrlHide{
display: none;
}


/*Adjust table in html of SP Shistory */
.nx-control-richtext-label.cssHistory table th {
  background-color: #FFD700 !important; /* bright yellow */
  color: #000 !important;
  font-weight: bold !important;
  padding: 8px !important;
  border: 1px solid #000 !important;
}

/*Disable Group Control */
.nx-group-control-container.cssGrpLOCK {
  opacity: 0.9;
  pointer-events: none;
  filter: grayscale(100%);
}

/*buttons inside the action-panel*/
ntx-action-panel button {
    background-color: #FFD700 !important;
    color: #000000 !important;
    font-weight: bold !important;
    border: none !important;
    border-radius: 6px !important;
}

/* Hide delete and add buttons only if inside a container with "cssRS" class */
.cssRS .ntx-repeating-section-remove-button,
.cssRS .btn-repeating-section-new-row {
  display: none !important;
}

.cssRS + .btn-repeating-section-new-row {
  display: none !important;
}

/* Target the specific multi-choice container */
.cssMchoice2cols .nx-checkbox-group {
    display: grid;                     /* use grid layout */
    grid-template-columns: repeat(2, 1fr); /* 2 equal columns */
    gap: 10px 20px;                    /* row-gap 10px, column-gap 20px */
}

/* Optional: make each checkbox label align nicely */
.cssMchoice2cols .nx-checkbox {
    display: flex;
    align-items: center;
    padding: 4px 0;
}


/* Print View 
.nx-sp-form-runtime .nx-command-bar
 {

    display:block;
}*/
/* ===== Target the 4-column group control ===== */
.cssGrp4cols {
    display: flex;
    flex-direction: column;
    width: 100%;
}

/* ===== Each row inside the group ===== */
.cssGrp4cols .nx-row {
    display: flex;
    flex-direction: row;
    border-bottom: 1px solid black !important; /* Row separator */
    margin: 0;
    padding: 1px;

}

/* ===== 1st column: ID (very narrow) ===== */
.cssGrp4cols .nx-row > div:nth-child(1) {
    flex: 0 0 22px;      /* Fixed width 10px */
    max-width: 22px;
    min-width: 22px;
    overflow: hidden;
    text-align: center;
    /*border-right: 1px solid black;  Column separator */

    white-space: nowrap;
    text-overflow: ellipsis;
}

/* ===== 2nd column: Description (wider) ===== */
.cssGrp4cols .nx-row > div:nth-child(2) {
    flex: 0 0 35%;        /* Adjust percentage as needed */
    min-width: 150px;

    padding-left: 7px;
    padding-right: 5px;
}

/* ===== 3rd column: Yes/No (medium) ===== */
.cssGrp4cols .nx-row > div:nth-child(3) {
    flex: 0 0 19;       /* Medium width */
    min-width: 60px;

    display: flex;
    align-items: center; /* Center radio buttons vertically */
    justify-content: center; /* Center horizontally */
}

/* ===== 4th column: Comments (widest) ===== */
.cssGrp4cols .nx-row > div:nth-child(4) {
    flex: 1;             /* Take remaining space */
    padding-left: 5px;
    padding-right: 5px;
}

/* ===== Optional: Hover effect for neatness ===== */
.cssGrp4cols .nx-row:hover {
    background-color: #f5f5f5;
}

/* ===== Optional: make inner form controls fit nicely ===== */
.cssGrp4cols .nx-row input,
.cssGrp4cols .nx-row textarea,
.cssGrp4cols .nx-row select {
    width: 100%;
    box-sizing: border-box;
}
/* Remove last row's bottom border */
.cssGrp4cols .nx-row:last-child {
  border-bottom: none !important;
}
