customCss.codeString:
/* Status colors */
.cssGreen > .nx-valign-middle > .nx-theme-richtext > div > div:first-child {
    font-weight: bold;
    background-color: #008000;
    border-radius: 6px;
    color: #fff;
    display: inline-block;
    font-family: Arial, sans-serif;
    padding: 5px 25px;
}
.cssOrange > .nx-valign-middle > .nx-theme-richtext > div > div:first-child {
    font-weight: bold;
    background-color: #f6a600;
    border-radius: 6px;
    color: #fff;
    display: inline-block;
    font-family: Arial, sans-serif;
    padding: 5px 25px;
}
.cssRed > .nx-valign-middle > .nx-theme-richtext > div > div:first-child {
    font-weight: bold;
    background-color: #EC2D0E;
    border-radius: 6px;
    color: #fff;
    display: inline-block;
    font-family: Arial, sans-serif;
    padding: 5px 25px;
}


/*Hide CTRL */
.ctrlHide{
display: none;
}


/*Adjust table in html of SP Shistory */
.nx-control-richtext-label.cssHistory table th {
  background-color: #FFD700 !important; /* bright yellow */
  color: #000 !important;
  font-weight: bold !important;
  padding: 8px !important;
  border: 1px solid #000 !important;
}

/*Disable Group Control */
.nx-group-control-container.cssGrpLOCK {
  opacity: 0.9;
  pointer-events: none;
  filter: grayscale(100%);
}

/*buttons inside the action-panel*/
ntx-action-panel button {
    background-color: #FFD700 !important;
    color: #000000 !important;
    font-weight: bold !important;
    border: none !important;
    border-radius: 6px !important;
}

/* Hide delete and add buttons only if inside a container with "cssRS" class */
.cssRS .ntx-repeating-section-remove-button,
.cssRS .btn-repeating-section-new-row {
  display: none !important;
}

.cssRS + .btn-repeating-section-new-row {
  display: none !important;
}

/* === Choice radio option as Buutons Base container === */
.cssChoicebtn .nx-radio-group {
  display: inline-flex;
  border-radius: 6px;
  overflow: hidden;
  border: 1px solid #c9a800;
}

/* === Hide default radio visuals === */
.cssChoicebtn .nx-radio-faux {
  display: none;
}

/* === Each radio option === */
.cssChoicebtn .nx-radio {
  margin: 0;
}

/* === Label styling === */
.cssChoicebtn .nx-radio label {
  cursor: pointer;
}

/* === Button appearance === */
.cssChoicebtn .nx-radio-container {
  padding: 5px 9px;
  background-color: #f3c600;        /* unselected yellow */
  color: #000;
  font-weight: 600;
  border-right: 1px solid #c9a800;
  transition: background-color 0.2s ease, color 0.2s ease;
  /* Center text */
  display: flex;
  justify-content: center; /* horizontal centering */
  align-items: center;     /* vertical centering */
  text-align: center;      /* fallback for inline text */
}

/* Remove divider from last button */
.cssChoicebtn .nx-radio:last-child .nx-radio-container {
  border-right: none;
}

/* === Selected state === */
.cssChoicebtn input[type="radio"]:checked + .nx-radio-container {
  background-color: #d6aa00;        /* darker selected yellow */
  color: #000;
}

/* === Hover effect === */
.cssChoicebtn .nx-radio-container:hover {
  background-color: #e2b800;
}

/* === Text alignment === */
.cssChoicebtn .nx-radio-label {
  white-space: nowrap;
}
/* Remove Nintex radio circle */
.cssChoicebtn .nx-radio-faux {
  display: none !important;
  }

